private void changeUrlOnError(Exception e) {
  if (e instanceof ConnectException) {
    clientIndex++;
    if (clientIndex > clients.size() - 1) {
      clientIndex = 0;
    }
    cusc.shutdownNow();
    cusc = new ErrorLoggingConcurrentUpdateSolrClient(((HttpSolrClient) clients.get(clientIndex)).getBaseURL(),
        httpClient, 30, 3);
  }
}

