public void addSpanQueries(SpanNearClauseFactory sncf) throws IOException {
  Query snq = getSpanNearQuery(sncf.getIndexReader(),
                                sncf.getFieldName(),
                                getWeight(),
                                sncf.getBasicQueryFactory());
  sncf.addSpanNearQuery(snq);
}

