        segmentInfos.read(directory);
        segmentInfos.clear();
        doCommit = false;
      } catch (IOException e) {
        // Likely this means it's a fresh directory
        doCommit = true;
      }

      if (doCommit) {
        // Only commit if there is no segments file in
        // this dir already.
        segmentInfos.commit(directory);
        synced.addAll(segmentInfos.files(directory, true));
      } else {
        // Record that we have a change (zero out all
        // segments) pending:
        changeCount++;
      }
    } else {
      segmentInfos.read(directory);

      IndexCommit commit = conf.getIndexCommit();
      if (commit != null) {
        // Swap out all segments, but, keep metadata in
        // SegmentInfos, like version & generation, to
        // preserve write-once.  This is important if
        // readers are open against the future commit
        // points.
        if (commit.getDirectory() != directory)
          throw new IllegalArgumentException("IndexCommit's directory doesn't match my directory");
        SegmentInfos oldInfos = new SegmentInfos();
        oldInfos.read(directory, commit.getSegmentsFileName());
        segmentInfos.replace(oldInfos);
        changeCount++;
        if (infoStream != null)
          message("init: loaded commit \"" + commit.getSegmentsFileName() + "\"");
      }

      // We assume that this segments_N was previously
      // properly sync'd:
      synced.addAll(segmentInfos.files(directory, true));
    }

    setRollbackSegmentInfos(segmentInfos);

    docWriter = new DocumentsWriter(directory, this, conf.getIndexingChain());
    docWriter.setInfoStream(infoStream);
    docWriter.setMaxFieldLength(maxFieldLength);

    // Default deleter (for backwards compatibility) is
    // KeepOnlyLastCommitDeleter:
    deleter = new IndexFileDeleter(directory,
                                   conf.getIndexDeletionPolicy(),
                                   segmentInfos, infoStream, docWriter);

    if (deleter.startingCommitDeleted)
      // Deletion policy deleted the "head" commit point.
      // We have to mark ourself as changed so that if we
      // are closed w/o any further changes we write a new
      // segments_N file.
      changeCount++;

    docWriter.setMaxBufferedDeleteTerms(conf.getMaxBufferedDeleteTerms());
    docWriter.setRAMBufferSizeMB(conf.getRAMBufferSizeMB());
    docWriter.setMaxBufferedDocs(conf.getMaxBufferedDocs());
    pushMaxBufferedDocs();

    if (infoStream != null) {
      messageState();
    }

  } catch (IOException e) {
    writeLock.release();
    writeLock = null;
    throw e;
  }
}

