public void set(long index) {
  assert index >= 0 && index < numBits: "index=" + index + " numBits=" + numBits;
  int wordNum = (int) (index >> 6);      // div 64
  int bit = (int) (index & 0x3f);     // mod 64
  long bitmask = 1L << bit;
  bits[wordNum] |= bitmask;
}

