private List<Explanation> explainConstantFactors() {
  List<Explanation> subs = new ArrayList<>();
  // scale factor
  subs.add(Explanation.match(k1 + 1, "scaling factor, k1 + 1"));
  // query boost
  if (boost != 1.0f) {
    subs.add(Explanation.match(boost, "boost"));
  }
  // idf
  subs.add(idf);
  return subs;
}

