  Analyzer a = new MockAnalyzer(random());
  AnalyzingSuggester suggester = new AnalyzingSuggester(a, a, 0, 256, -1);
  suggester.build(new TermFreqArrayIterator(new TermFreq[] {
      new TermFreq("а где Люси?", 7),
  }));
