ExactBM25DocScorer(BM25Stats stats, DocValues norms) throws IOException {
  assert norms != null;
  this.stats = stats;
  this.weightValue = stats.weight * (k1 + 1); // boost * idf * (k1 + 1)
  this.cache = stats.cache;
  this.norms = (byte[])norms.getSource().getArray();
}

