public static boolean getAttribute(Element element, String attributeName,
		boolean deflt)
{
	String result = element.getAttribute(attributeName);
	if ((result == null) || ("".equals(result)))
	{
		return deflt;
	}
	return Boolean.getBoolean(result);
}

