private int refill() throws IOException {
  int doc = scanTo(0);

  int bufferSize = Math.min(docs.length, limit - ord);
  start = -1;
  count = bufferSize;
  ord += bufferSize;

  if (omitTF)
    fillDocs(bufferSize);
  else
    fillDocsAndFreqs(bufferSize);

  return doc;
}

