final void commitPendingFiles() {
  if (pending != null) {
    if (deletable == null) {
      deletable = pending;
      pending = null;
    } else {
      deletable.addAll(pending);
      pending = null;
    }
  }
}

final void commitPendingFiles() throws IOException {
  if (pending != null) {
    if (deletable == null) {
      deletable = new Vector();
    }
    Iterator it = pending.iterator();
    while(it.hasNext()) {
      deletable.addElement(it.next());
    }
    pending = null;
    deleteFiles();
  }
}

