public static void createIndex() {
  for (int i = 0; i < NUM_DOCS; i++) {
    assertU(adoc("id", Integer.toString(i), "name", "a" + i + " b" + i + " c" + i + " d"+i + " e" + i));
    if (random().nextInt(NUM_DOCS) == 0) {
      assertU(commit());  // sometimes make multiple segments
    }
  }
  assertU(commit());
}

