/**
 * Tell all {@link ResourceLoaderAware} instances about the loader
 */
public void inform( ResourceLoader loader ) 
{
  for( ResourceLoaderAware aware : waitingForResources ) {
    aware.inform( loader );
  }
  waitingForResources.clear();
}

