/** Does initial setup for a merge, which is fast but holds
 *  the synchronized lock on IndexWriter instance.  */
final synchronized void mergeInit(MergePolicy.OneMerge merge) throws IOException {
  boolean success = false;
  try {
    _mergeInit(merge);
    success = true;
  } finally {
    if (!success) {
      mergeFinish(merge);
    }
  }
}

