/**
 * Intersects the data with a given {@link IntHashSet}.
 * 
 * @param set
 *            A given ArrayHashSetInt which holds the data to be intersected
 *            against
 */
public void intersect(IntHashSet set) {
  int newSize = 0;
  for (int i = 0; i < size; ++i) {
    if (set.contains(data[i])) {
      data[newSize] = data[i];
      ++newSize;
    }
  }
  this.size = newSize;
}

