public synchronized String segString() {
  StringBuffer buffer = new StringBuffer();
  for(int i = 0; i < segmentInfos.size(); i++) {
    if (i > 0) {
      buffer.append(' ');
    }
    buffer.append(segmentInfos.info(i).name + ":" + segmentInfos.info(i).docCount);
  }

  return buffer.toString();
}

