private void writeLabel(DataOutput out, int v) throws IOException {
  assert v >= 0: "v=" + v;
  if (inputType == INPUT_TYPE.BYTE1) {
    assert v <= 255: "v=" + v;
    out.writeByte((byte) v);
  } else if (inputType == INPUT_TYPE.BYTE2) {
    assert v <= 65535: "v=" + v;
    out.writeShort((short) v);
  } else {
    out.writeVInt(v);
  }
}

